// Aqui devemos criar nosso contexto e nosso provider.
import { createContext, useState }  from "react";

export const PokeCenterContext = createContext();

export const PokeCenterContextProvider = ({children}) => {

    const [treiner, setTreiner] = useState([{
        nome: "",
        sobrenome: "",
        email: "",
        nomePokemon: ""
    }]);

    const handleBlur = (e) => {
        setTreiner()
    }

    return(
        <PokeCenterContext.Provider value={{treiner, handleBlur}}>
            {children}
        </PokeCenterContext.Provider>
    )

};
